function init() {
	SXE.initElementDialog('abbr');
	if (SXE.currentAction == "update") {
		SXE.showRemoveButton();
	}
}

function insertAbbr() {
    var selected = $(document.forms[0]).getInputs("radio").find(function(radio) { return radio.checked; });
    $('title').value = selected.next().value;
	SXE.insertElement(tinymce.isIE ? 'html:abbr' : 'abbr', selected.value);
	tinyMCEPopup.close();
}

function removeAbbr() {
	SXE.removeElement('abbr');
	tinyMCEPopup.close();
}

tinyMCEPopup.onInit.add(init);
